package com.yappy.trnxd.backend.transaction.junior.command;public interface CommonCommand <RQ, RT> {    /**     * Execute the command with the given request.     *     * @param request the request     * @return the response     */    RT execute(RQ request);    /**     * Execute the fallback logic in case of failure.     *     * @param request the request     * @return the fallback response     */    Throwable executeFallback(RQ request);}