package com.yappy.trnxd.backend.transaction.junior.controller;import io.swagger.v3.oas.annotations.Operation;import jakarta.servlet.http.HttpServletRequest;import org.springframework.http.MediaType;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/api/v1/transaction")public class TransactionController {    @GetMapping(path = "/send", produces = MediaType.APPLICATION_JSON_VALUE)    @Operation(summary = "Send Transaction", description = "Endpoint to send a transaction")    public String sendTransaction(HttpServletRequest headers) {        return "Transaction sent successfully!";    }}