package com.yappy.trnxd.backend.transaction.junior.cores;import com.fasterxml.jackson.databind.ObjectMapper;import com.yappy.trnxd.backend.transaction.junior.cores.personal_payment.command.BeginCommand;import com.yappy.trnxd.backend.transaction.junior.cross_model.model.BeginTransactionDTO;import com.yappy.trnxd.backend.transaction.junior.cross_model.model.TransactionRequestEntity;import com.yappy.trnxd.backend.transaction.junior.cross_model.model.TransactionResponseEntity;import com.yappy.trnxd.backend.transaction.junior.library.utils.CommonHeaderUtils;import io.swagger.v3.oas.annotations.Operation;import jakarta.servlet.http.HttpServletRequest;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.MediaType;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@Slf4j@RestController@RequestMapping("/api/v1/transaction")public class TransactionController {    @Autowired    private ObjectMapper objectMapper;    @Autowired    private BeginCommand p2pCommand;    @Autowired    private CommonHeaderUtils commonHeaderUtils;    @PostMapping(path = "/begin/p2p", produces = MediaType.APPLICATION_JSON_VALUE)    @Operation(summary = "Send Transaction")    public TransactionResponseEntity<BeginTransactionDTO> sendTransaction(@RequestBody TransactionRequestEntity<BeginTransactionDTO> request,                                                                          HttpServletRequest headers) {        log.info("Request: {}", headers);        request.setProfile(commonHeaderUtils.getProfileDTO(headers));        return p2pCommand.execute(request, request.getProfile());    }}