package com.yappy.trnxd.backend.transaction.junior.enums;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import org.springframework.http.HttpStatus;@Getter@AllArgsConstructor@NoArgsConstructorpublic enum KafkaResponseCatalog {    LISTENING_TO_TOPIC("LISTENING_TO_TOPIC", "{} - Listening to messages on topic: {}", HttpStatus.OK),    MESSAGE_RECEIVED("MESSAGE_RECEIVED", "Message received: {}", HttpStatus.OK),    MESSAGE_POSTED_CORRECTLY("SUCCESS", "Message sent to topic {}: {}", HttpStatus.OK),    INVALID_MESSAGE("INVALID_MESSAGE", "Invalid message format", HttpStatus.BAD_REQUEST),    PROCESSING_ERROR("PROCESSING_ERROR", "Error processing message", HttpStatus.INTERNAL_SERVER_ERROR),    TIMEOUT("TIMEOUT", "Message processing timed out", HttpStatus.REQUEST_TIMEOUT),    UNAUTHORIZED("UNAUTHORIZED", "Unauthorized access to Kafka topic", HttpStatus.UNAUTHORIZED),    TOPIC_NOT_FOUND("TOPIC_NOT_FOUND", "Kafka topic not found", HttpStatus.NOT_FOUND);    private String code;    private String message;    private HttpStatus status;}