package com.yappy.trnxd.backend.transaction.junior.enums;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import org.springframework.http.HttpStatus;@Getter@AllArgsConstructor@NoArgsConstructorpublic enum TransactionError {    INVALID_AMOUNT("TXN-001", "Invalid amount", HttpStatus.BAD_REQUEST),    INSUFFICIENT_FUNDS("TXN-002", "Insufficient funds", HttpStatus.PAYMENT_REQUIRED),    USER_NOT_FOUND("TXN-003", "User not found", HttpStatus.NOT_FOUND),    TRANSACTION_NOT_FOUND("TXN-004", "Transaction not found", HttpStatus.NOT_FOUND),    UNAUTHORIZED_ACCESS("TXN-005", "Unauthorized access", HttpStatus.UNAUTHORIZED);    private String code;    private String message;    private HttpStatus status;}